INSERT INTO `versions` (`id`, `item_type`, `item_id`, `event`, `whodunnit`, `object`, `locale`, `created_at`)
VALUES
	(1,'Rhinoart::PageContent',1,'create',NULL,NULL,NULL,'2015-02-18 09:03:15'),
	(2,'Rhinoart::PageField',1,'create',NULL,NULL,NULL,'2015-02-18 09:03:15'),
	(3,'Rhinoart::PageField',2,'create',NULL,NULL,NULL,'2015-02-18 09:03:15'),
	(4,'Rhinoart::PageField',3,'create',NULL,NULL,NULL,'2015-02-18 09:03:15'),
